import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import axios from "axios";
import Post from "./Post";
import "./Community.css";
import CommentForm from "./CommentForm";

function Community() {
  const [showCommentForm, setShowCommentForm] = useState(false);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/posts/");
        const promises = response.data.map((post) =>
          axios.get(`http://127.0.0.1:8000/api/posts/${post.id}/comments/`)
        );
        const commentResponses = await Promise.all(promises);
        const postsWithComments = response.data.map((post, index) => ({
          ...post,
          comments: commentResponses[index].data,
        }));
        setPosts(postsWithComments);
        console.log(postsWithComments);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  const handlePostDelete = async (postId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/posts/${postId}/delete/`);
      const updatedPosts = posts.filter((post) => post.id !== postId);
      setPosts(updatedPosts);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const handleCommentDelete = async (postId, commentId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/posts/${postId}/comments/${commentId}/delete/`);
      const updatedPosts = posts.map((post) => {
        if (post.id === postId) {
          const updatedComments = post.comments.filter((comment) => comment.id !== commentId);
          return {
            ...post,
            comments: updatedComments,
          };
        } else {
          return post;
        }
      });
      setPosts(updatedPosts);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCommentCreated = (postId, comment) => {
    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          comments: [...post.comments, comment],
        };
      } else {
        return post;
      }
    });
    setPosts(updatedPosts);
    window.location.reload(true);

  };

  return (
    <>
      <Navbar />
      <>
      <div>
    {posts.map((post) => (
      <div key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.content}</p>
        {/* <button onClick={() => handlePostDelete(post.id)}>Delete Post</button> */}
        {post.comments.map((comment) => (
          <div key={comment.id}>
            <p>{comment.content}</p>
            <button onClick={() => handleCommentDelete(post.id, comment.id)}>Delete Comment</button>
          </div>
        ))}
      </div>
    ))}
  </div>
        <Post onPostCreated={handlePostCreated} />
        <div className="post_content">
          {posts.map((post) => (
            <>
              <div key={post.id}>
                <div className="post_body">
                  <h3>
                    Author: <span> {post.user?.name} </span>
                    <span className="create_time">{post.created_at}</span>
                  </h3>
                  
                </div>
                <div>
                  <h3>Title:</h3>
                  <h2>{post.title}</h2>
                </div>
                <h3>Content:</h3>
                <h2>{post.content}</h2>
                <button onClick={() => handlePostDelete(post.id)}>Delete Post</button>
              </div>
              <div>
                {showCommentForm === post.id && (
                  <CommentForm
                    postId={post.id}
                    onSubmit={(comment) =>
                      handleCommentCreated(post.id, comment)
                    }
                  />
                )}
              </div>
              <div>
                <ul className="com">
                  <lo>
                    <button
                      onClick={() => setShowCommentForm(post.id)}
                      className="com"
                    >
                      <i className="fas fa-comment-alt comment"></i> Add Comment
                    </button>
                  </lo>
                  {post.comments?.map((comment) => (
                    <div className="commentlist" key={comment.id}>
                      {comment.user && <h3>Commenter: {comment.user?.name}</h3>}
                      <p>{comment.content}</p>
                    </div>
                  ))}
                </ul>
              </div>
              <hr />
            </>
          ))}
        </div>
      </>
      <Footer />
    </>
  );
}

export default Community;
