//             users.push(userData);
//             localStorage.setItem("data", JSON.stringify(users));
//             setError("");
//             // Login successful, redirect to home page
//             window.location.href = "/";
//         } else {
//             // Login failed, show error message here
//             console.log(data)
//             setError("Failed to login. Please check your username and password.");
//         }
//     })
//     .catch(error => {
//         // Handle any errors here
//         setError("An error occurred while trying to login.");
//     });
// };
// setUserid("");

const validationName = () => {
  const regx = /^\w{3,8}$/;
  if (regx.test(name)) {
    return true;
  } else {
    setError("Name Invalid...!");
    return false;
  }
};

const validationEmail = () => {
  const regx = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
  if (regx.test(email)) {
    return true;
  } else {
    setError("Email Invalid...!");
    return false;
  }
};

// const validationPassword = () => {
//   const regx = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,}$/;
//   // const regx = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
//   if (regx.test(password)) {
//     return true;
//   } else {
//     setError('Password must contain at least one digit, one lowercase letter, one uppercase letter, one special character, be at least 8 characters');
//     return false;
//   }
// };

  const handleAlertClick = () => {
    swal({
      title: "مــــعــــلــــــش هه",
      timer: 4000,
    });
  };

  return (
    <>
      <section className="section">
      <div className="contain">
        {error && <div className="alert alert-danger">{error}</div>}
        <img src={icon} alt="" />
        <form
          action=""
          className="form-class"
          method="post"
          onSubmit={handleSubmit}
        >
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Enter Your Name..."
            className=" form-input"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            name="email"
            id="email"
            placeholder="Enter Your Email..."
            className=" form-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            name="password"
            id="password"
            placeholder="Enter Your Password..."
            className=" form-input form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className="login">
            <button type="submit" className="login-btn">Login</button>
            <Link to="/signup" className="login-btn">Signup</Link>

            <a href="#" id="alert" onClick={handleAlertClick}>
              عارف انك نسيت كلمه السر؟
            </a>
          </div>
        </form>
      </div>
      </section>
      
    </>
  );
}
