/* eslint-disable jsx-a11y/anchor-is-valid */
import React from "react";
import "./Login.css";
import "bootstrap/dist/css/bootstrap.min.css";
import icon from "../image/icon.png";
import swal from "sweetalert";
import { useState } from "react";
import {useNavigate} from "react-router-dom";

export default function Login() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  // const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validationName() && validationEmail() && validationPassword()) {
        const userData = {
            name,
            email,
            password,
        };
        let users = JSON.parse(localStorage.getItem("data")) || [];
        users.push(userData);
        console.log(users);
        localStorage.setItem("data", JSON.stringify(users));
        window.open("/home", "_self");
        setError("");
    }
};

const validationName = () => {
    const regx = /^(\s)[A-Za-z]+((\s)?(('|-|.|_)?([A-Za-z])+))(\s)$/;
    if (regx.test(name)) {
        return true;
    } else {
        setError("Name Invalid...!");
        return false;
    }
};

const validationEmail = () => {
    const regx = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
    if (regx.test(email)) {
        return true;
    } else {
        setError("Email Invalid...!");
        return false;
    }
};

const validationPassword = () => {
    const regx = /^(?=.[A-Za-z])(?=.\d)(?=.[@$!%#?&])[A-Za-z\d@$!%#?&]{8,}$/;
    if (regx.test(password)) {
        return true;
    } else {
        setError("Password Invalid...!");
        return false;
    }
};

  const handleAlertClick = () => {
    swal({
      title: "مــــعــــلــــــش هه",
      timer: 4000,
    });
  };
  return (
    <>
      <section className="section">
      <div className="contain">
        {error && <div className="alert alert-danger">{error}</div>}
        <img src={icon} alt="" />
        <form
          action=""
          className="form-class"
          method="post"
          onSubmit={handleSubmit}
        >
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Enter Your Name..."
            class=" form-input"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            name="email"
            id="email"
            placeholder="Enter Your Email..."
            class=" form-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            name="password"
            id="password"
            placeholder="Enter Your Password..."
            class=" form-input form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className="login">
            <button type="submit" className="login-btn">
              login
            </button>
            <a href="#" id="alert" onClick={handleAlertClick}>
              عارف انك نسيت كلمه السر؟
            </a>
          </div>
        </form>
      </div>
      </section>
    </>
  );
}
