import React, { useState, useEffect } from "react";
import axios from 'axios';
function CommentForm({ postId, onCommentCreated }) {
  const [comment, setComment] = useState("");
  const handleSubmit = (event) => {
    event.preventDefault();
    const newComment = {
      content: comment,
      author: "Anonymous", // or set the author based on user authentication
    };
    axios.post(`http://127.0.0.1:8000/api/posts/${postId}/comments/`, newComment)
      .then((response) => {
        onCommentCreated(postId, response.data);
        setComment("");
      })
      .catch((error) => console.error(error));
  };
  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="comment">Comment:</label>
      <input
        type="text"
        id="comment"
        value={comment}
        onChange={(event) => setComment(event.target.value)}
      />
      <button type="submit">Add Comment</button>
    </form>
  );
}
export default CommentForm;
