import React, { useState } from 'react';
import axios from 'axios';

function FacebookPost({ onSubmit }) {
  const [post, setPost] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(post);
    setPost('');
  };

  return (
    <div className="post-container">
      <form onSubmit={handleSubmit}>
        <textarea
          value={post}
          onChange={(e) => setPost(e.target.value)}
          placeholder="What's on your mind?"
        />
        <button type="submit">Post</button>
      </form>
    </div>
  );
}

function FacebookComment({ onSubmit }) {
  const [comment, setComment] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(comment);
    setComment('');
  };

  return (
    <div className="comment-container">
      <form onSubmit={handleSubmit}>
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Write a comment..."
        />
        <button type="submit">Comment</button>
      </form>
    </div>
  );
}

function Community() {
  const [posts, setPosts] = useState([]);

  const handlePostSubmit = (post) => {
    // Code to submit post to server or perform other actions
    console.log('Post submitted:', post);
  };

  const handleCommentSubmit = (comment) => {
    // Code to submit comment to server or perform other actions
    console.log('Comment submitted:', comment);
  };

  // eslint-disable-next-line no-undef
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/posts/')
      .then((response) => setPosts(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <>
      <div className="post-section">
        <FacebookPost onSubmit={handlePostSubmit} />
      </div>
      <div className="posts-container">
        {posts.map((post) => (
          <div key={post.id}>
            <h2>{post.title}</h2>
            <p>{post.content}</p>
            <p>Author: {post.author}</p>
            <div className="comments-section">
              {post.comments.map((comment) => (
                <div key={comment.id}>
                  <p>{comment.content}</p>
                  <p>Author: {comment.author}</p>
                </div>
              ))}
              <FacebookComment onSubmit={handleCommentSubmit} />
            </div>
            <hr />
          </div>
        ))}
      </div>
    </>
  );
}

export default Community;
