import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import axios from "axios";
import Post from "./Post";
import "./Community.css";
import CommentForm from "./CommentForm";

function Community() {
  const [showCommentForm, setShowCommentForm] = useState(false);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/posts/");
        const promises = response.data.map((post) =>
          axios.get(`http://127.0.0.1:8000/api/posts/${post.id}/comments/`)
        );
        const commentResponses = await Promise.all(promises);
        const postsWithComments = response.data.map((post, index) => ({
          ...post,
          comments: commentResponses[index].data,
        }));
        setPosts(postsWithComments);
        console.log(postsWithComments);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const handleCommentCreated = (postId, comment) => {
    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          comments: [...post.comments, comment],
        };
      } else {
        return post;
      }
    });
    setPosts(updatedPosts);
  };

  return (
    <>
      <Navbar />
      <>
        <Post onPostCreated={handlePostCreated} />
        <div className="post_content">
          {posts.map((post) => (
            <>
              <div key={post.id}>
                <h3>Author: {post.user?.name}</h3>
                <h3>{post.created_at}</h3>
                <h2>{post.title}</h2>
                <p>{post.content}</p>
              </div>
              <div>
                {showCommentForm === post.id && (
                  <CommentForm
                    postId={post.id}
                    onSubmit={(comment) =>
                      handleCommentCreated(post.id, comment)
                    }
                  />
                )}
              </div>
              <div>
                <ul className="com">
                  <li>
                    <button
                      onClick={() => setShowCommentForm(post.id)}
                      className="com"
                    >
                      <i className="fas fa-comment-alt comment"></i> Add Comment
                    </button>
                  </li>
                  {post.comments?.map((comment) => (
                    <div key={comment.id}>
                      {comment.user && (
                        <h5> Commenter: {comment.user?.name}</h5>
                      )}
                      <li>{comment.content}</li>
                    </div>
                  ))}
                </ul>
              </div>
              <hr />
            </>
          ))}
        </div>
      </>
      <Footer />
    </>
  );
}

export default Community;
