import React from 'react';
import icon from "../image/icon.png";
import "./Home.css";
// import "./main.css";
import {useNavigate} from "react-router-dom";
import { useState,useEffect } from 'react';
import img1 from "../image/1.jpg";
import img3 from "../image/3.jpg";

export default function Home() {
// -----------------------
const [userLogin, setUserLogin] = useState("");
useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
    userData.forEach((user) => {
      setUserLogin(user.name);
    });
  });
  const Navigate = useNavigate();
  const handleogout = () => {
    localStorage.clear("data");
    Navigate("/");
  };

    window.addEventListener('scroll',function(){
        let windowScroll =window.scrollY
        if(windowScroll > 100){
            ('.navbar').animate({'top':'0px' , 'width':'100%'})
            ('.to-up').css('display', 'flex')
        }
        else if (windowScroll < 100)
        {
            ('.navbar').animate({'top':'50px','width':'70%'})
            ('.to-up').css('display', 'none')
        }
        ('.to-up').click(()=>window.scrollTo(0,0)  )
    })

//     // ---------------------------- random
    let random = document.querySelectorAll('#review-random')

    function randomReviews(){
        for (let i = 0; i < random.length; i++) {
            random[i].innerHTML =  Math.round(Math.random()*5000)+`K`
            
        }
    }
    setInterval( randomReviews , 2000)

    // ------------------------------


    // ----------------- Loading 
    // (document).ready(()=>{
    //     ('#isLoading img').fadeOut(2000 ,()=>{
    //         ('#isLoading').slideUp(2000 ,()=>{
    //             ('#isLoading').remove()
    //             ('body').css('overflow','auto')
    //         })
    //     })
    //     })
    const [loading, setloading] = useState(false);
    useEffect(() => {
    setloading(true);
    setTimeout(() => {
      setloading(false);
    }, 4000);
  }, []);

    return (
        <>
            {loading ? (<div className="isLoading">
            <img  src={icon} alt="" className="fa-spin"></img>
            </div>):(
                <>
                <div className="to-up position-fixed end-0 bottom-0 m-5 fa-2x">
                            <i className=" fas fa-arrow-alt-circle-up"></i>
                </div>
                <nav className="navbar navbar-expand-lg p-2 navbar-dark">
                    <div className="container">
                        <a className="navbar-brand fa-2xl" href="#home"><img style={{width: "3rem"}} src={icon} alt=""/></a>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse fs-5" id="navbarSupportedContent">
                            <ul className="navbar-nav mx-auto mb-2 mb-lg-0">
                                <li className="nav-item">
                                    <a className="nav-link active" aria-current="page" href="#home">Home</a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#about">About</a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#category">Categories</a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="Movies.html">Movies</a>
                                </li>
                            </ul>
                            <span className="fw-bold mx-4" id="login"></span>
                            <button className="btn btn-outline-dark btn-sm border-0 fw-bold fs-5" >Hi {userLogin}</button>
                            <button className="btn btn-outline-dark btn-sm border-0 fs-5" id="logOutBtn"onClick={handleogout}>LogOut</button>
                        </div>
                    </div>
                </nav>
                <div className='home d-flex align-items-center justify-content-center text-light' id='home'>
                    <div className='home_contnet'>
                        <h1>Welcome To <span>Movies</span> Hub</h1>
                        <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Deleniti aliquam animi dolore debitis at dolorme?</p>
                    </div>
                </div>
                <section class="about bg-light py-5" id="about">
        <div class="container">
            <div class="about-caption py-5 text-center ">
                <p class="text-muted">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Similique earum vel
                    doloremque
                    neque
