import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import axios from "axios";
import Post from "./Post";
import "./Community.css";

function Community() {
  const [posts, setPosts] = useState([]);
  const [user, setUserName] = useState("");

  // useEffect(() => {
  //   const userData = JSON.parse(localStorage.getItem("data")) || [];
  //   userData.forEach((user) => {
  //     setUserName(user.name);
  //   });

  //   axios.get("http://127.0.0.1:8000/api/posts/")
  //        .then((response) => setPosts(response.data))
  //        .catch((error) => console.error(error));

  //   axios.get("http://127.0.0.1:8000/api/comments/")
  //        .then((response) => setPosts(response.data))
  //        .catch((error) => console.error(error));     
  // }, []);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("data")) || [];
  
    axios
      .get("http://127.0.0.1:8000/api/posts/")
      .then((response) => {
        // map over the response data and add the name of the author to each post
        const postsWithAuthorName = response.data.map(post => ({
          ...post,
          authorName: userData.find(user => user.id === post.author)?.name || "Unknown"
        }));
  
        setPosts(postsWithAuthorName);
      })
      .catch((error) => console.error(error));
  }, []);
  

  const handlePostCreated = (post) => {
    setPosts([...posts, post]);
  };

  const handleCommentCreated = (postId, comment) => {
    const newPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          comments: [...post.comments, comment],
        };
      }
      return post;
    });
    setPosts(newPosts);
  };

  return (
    <>
      <Navbar />
      <>
        <Post onPostCreated={handlePostCreated} />
        <div className="post_content">
          {/* <p>Welcome</p>
            <div>hi</div> */}
          {posts.map((post) => (
            <>
              <div key={post.id}>
                <h3>Author: {user.name}</h3>
                <h2>{post.title}</h2>
                <p>{post.content}</p>

                <hr />
              </div>
              <div>
                {/* postId={post.id}
                comments={Comment.title}
                onCommentCreated={handleCommentCreated} */}
              </div>
            </>
          ))}
        </div>
      </>
      <Footer />
    </>
  );
}

export default Community;
