import React, { useState,useNavigate } from "react";
import "./signup.css";
import axios from 'axios';


function Signup() { 
    const [firstname, setFirstName] = useState("");
    const [lastname, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();
    const handleSubmit = (e) => {
      e.preventDefault();
      axios.post('/api/signup/', {
              email: email,
              password: password,
            })
              .then(response => {
                console.log(response.data);
                // show a success message to the user
              })
              .catch(error => {
                console.log(error);
                // show an error message to the user
              });
      if (validationFirstName &&validationLastName &&validationEmail()) {
        const userData = {
          firstname,
          lastname,
          email,
          password,
        };
        let users = JSON.parse(localStorage.getItem("data")) || [];
        users.push(userData);
        localStorage.setItem("data", JSON.stringify(users));
        navigate("/home");
        setError("");
      }
    };
  
    const validationFirstName = () => {
      const regx = /^\w{3,8}$/;
      if (regx.test(firstname)) {
        return true;
      } else {
        setError("Name Invalid...!");
        return false;
      }
    };
  
    const validationLastName = () => {
      const regx = /^\w{3,8}$/;
      if (regx.test(lastname)) {
        return true;
      } else {
        setError("Name Invalid...!");
        return false;
      }
    };
  
    const validationEmail = () => {
      const regx = /^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/;
      if (regx.test(email)) {
        return true;
      } else {
        setError("Email Invalid...!");
        return false;
      }
    };

//   const handleSubmit = (event) => {
//     event.preventDefault();
//     
//   }
//     // Here you can add your logic for handling form submission

  return (
    <form className="form" onSubmit={handleSubmit}>
      <div  className="signup">
      <div>
        <input
          type="text"
          id="firstName"
          name="firstName"
          placeholder="Enter First Name:"
          value={firstname}
          onChange={(event) => setFirstName(event.target.value)}
        />
      </div>
      <div>
        <input
          type="text"
          id="lastName"
          name="lastName"
          placeholder="Enter Last Name:"
          value={lastname}
          onChange={(event) => setLastName(event.target.value)}
        />
      </div>
      <div>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Enter Your E-mail:"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
      </div>
      <div>
        <input
        placeholder="Enter Your Password"
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
      </div>
      <div className="bottom">
        <button type="submit">Sign up</button>
        <a href="/">already have an account!</a>
      </div>
      </div>
    </form>
  );
}

export default Signup;
